import 'package:avanpost_retraining/pages/classes/class_preview_model.dart';
import 'package:avanpost_retraining/pages/classes/classes_states.dart';
import 'package:avanpost_retraining/requests/classes_requests.dart';
import 'package:flutter_bloc/flutter_bloc.dart';

class ClassesCubit extends Cubit<ClassesState> {
  ClassesCubit() : super(ClassesLoading()) {
    getCategories();
  }

  getCategories() async {
    //await ClassesRequests.getClasses();
    final cats = [
      ClassPreviewModel(
        imageUrl:
            'data:image/jpeg;base64,',
        name: 'Train',
      ),
      ClassPreviewModel(
        imageUrl:
            'https://cf-images.us-east-1.prod.boltdns.net/v1/static/3281700261001/a0d231cc-a242-48bd-a8dc-b0956cf36b63/699ec464-b3e3-462a-a2ea-ac4e74e9196a/1280x720/match/image.jpg',
        name: 'Plane',
      ),
      ClassPreviewModel(
        imageUrl:
            'https://upload.wikimedia.org/wikipedia/commons/thumb/6/63/LT_471_%28LTZ_1471%29_Arriva_London_New_Routemaster_%2819522859218%29.jpg/1200px-LT_471_%28LTZ_1471%29_Arriva_London_New_Routemaster_%2819522859218%29.jpg',
        name: 'Bus',
      )
    ];

    emit(ClassesLoaded(models: cats));
  }

  addClass(String className) {
    if (state is ClassesLoaded) {
      emit(
        ClassesLoaded(
          models: (state as ClassesLoaded).models +
              [
                ClassPreviewModel(
                  imageUrl: 'assets/placeholder.jpg',
                  name: className,
                ),
              ],
        ),
      );
    }
  }
}
